import { useStore } from './zustand'
import ReactHowler from 'react-howler'
import React from 'react'


const Player = () => {

  state = {
      bears: 0,
      playing: false
  }

  const statez = useStore.getState()
  console.log("PLAYER bears status" + statez.bears)
  console.log("PLAYER radio status" + statez.playing)
  this.state.bears = statez.bears
  this.state.playing = statez.playing

  return (
    <div className="player">
        <ReactHowler
        src='http://jazzradio.ice.infomaniak.ch/jazzradio-high.mp3'
        format= {['mp3', 'aac']}
        html5={true}
        playing={this.state.playing}
        />
    </div>
  );
};

export default Player
