import React, { createContext } from 'react'
import create from 'zustand'

export const useStore = create(set => ({
    bears: 3,
    playing: false,
    increasePopulation: () => set(state => ({ bears: state.bears + 1 })),
    removeAllBears: () => set({ bears: 0 }),
    playRadio: () => set({ playing: true }),
    pauseRadio: () => set({ playing: false })
}))

const zustandContext = createContext({
    radio: "",
    setOnoff: () => {}
  })


export function BearCounter() {
    const bears = useStore(state => state.bears)
    console.log("BEARS" + bears)
    return bears
}


class Zustand extends Component {
    constructor(props){
        super(props)
        this.state = {
            radio: "off", // une valeur de départ
            setOnoff: radio => this.setState({ radio: radio }) // nouvelle propriété de mutation
        };
    }
    render() {
        return (
          // Ici, rien ne change !
          <zustandContext.Provider value={this.state}>
            {this.props.children}
          </zustandContext.Provider>
        );
    }
}

export default Context
