import { ADD_RADIO, REMOVE_RADIO } from "./Types";

let nextRadioId = 0;

export const addRadio = content => ({
  type: ADD_RADIO,
  payload: {
    id: ++nextRadioId,
    content
  }
});

export const removeRadio = id => ({
  type: REMOVE_RADIO,
  payload: { id }
});
