import { useStore } from '../../zustand'
import ReactHowler from 'react-howler'
import React, { Component } from 'react'

class Player extends Component {
    constructor(props){
        super(props)
        this.state = {
            bears: 0,
            playing: false
        }
    }
   Getdata = () => {
        const statez = useStore.getState()
        if ( statez.playing != this.state.playing || statez.bears != this.state.bears ) {
            this.setState({
                playing: statez.playing,
                bears: statez.bears
            })
        }
        console.log("Player radio updated --")
        console.log("PLAYER radio state " + this.state.playing)
        console.log("PLAYER radio store " + statez.playing)

        return true
  }
  shouldComponentUpdate(nextProps, nextState){
    console.log('shouldComponentUpdate through')
    this.Getdata()
    return this.props.hintPlayerUpdate != nextProps.hintPlayerUpdate 
  }
  render(){
    return (
        <div className="player">
                <ReactHowler
                src='http://jazzradio.ice.infomaniak.ch/jazzradio-high.mp3'
                format= {['mp3', 'aac']}
                html5={true}
                playing={this.state.playing}
                />
        </div>
    );
  }
}
export default Player
