import React from 'react';
import { Navbar } from "react-bootstrap";
import { Nav } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { MDBIcon } from 'mdbreact';


import { useContext } from 'react';
import UserContext from './../context/UserContext';

function NavBar() { 

    const { userData, setUserData } = useContext(UserContext);

    const logout = () => {
        localStorage.removeItem('auth-token');
        setUserData({
            token: null,
            user: null
        });
    }

    const mystyle = {
        color: "LightSlateGray",
        fontWeight: 'bold',
        fontFamily: "Arial"
    };

    return(
        <Navbar collapseOnSelect expand="lg" style={{backgroundColor: 'transparent'}}>
        <Navbar.Brand href="#home" style={mystyle} >
            Widget_Stic
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="mr-auto">
            </Nav>
            <Nav>
                {
                userData.user ? (
                <>
                <Nav.Link eventKey={3} href="" class="profile" style={mystyle}>
                    Profile {userData.user.username}    
                </Nav.Link>
                <Nav.Link eventKey={3} onClick={logout} href="/" class="logout" style={mystyle} > 
                    Logout
                </Nav.Link>
                </>
                ) : (
                    <>
                <Nav.Link href="/Register" style={mystyle} >
                    Register
                </Nav.Link>
                <MDBIcon icon="sign-in-alt" />
                {/* <br /> */}
                <Nav.Link eventKey={2} href="/Login" style={mystyle} >
                    Login
                </Nav.Link>
                </>
                )}
            </Nav>
        </Navbar.Collapse>
        </Navbar>
    );
}

export default NavBar
