import React, { Component } from "react";
import { connect } from "react-redux";
import { Router, Switch, Route, Link } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";
import 'bootstrap/dist/js/bootstrap.min.js'
import "./App.css";

import axios from "axios"
//notifications
import ReactNotification from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'
import { store } from 'react-notifications-component';

import Login from "./components/login.component";
import Register from "./components/register.component";
// import Home from "./components/home.component";
import Profile from "./components/profile.component";
import BoardUser from "./components/board-user.component";
// import BoardModerator from "./components/board-moderator.component";
// import BoardAdmin from "./components/board-admin.component";
import Home from "./components/Home.jsx"
import Test from "./components/Test.jsx"
import Form from "./components/Test2.jsx"
import Radio from "./components/Radio.jsx"
import Player from "./components/radio/Player.jsx"

import { logout } from "./actions/auth";
import { clearMessage } from "./actions/message";

import { history } from './helpers/history';


class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
      snow: 0,
      terraincolor: '#667306',
      fieldcolor: '#f5be18',
      treecolor: '#076931',
      rain: 0,
      state: 0,
      numberclouds: 0,
      roadcolor: '#544c3e',
      town:'lyon',
      bears: 0,
      hintPlayerUpdate: 0,
      playing: false,
      radiocounter: 0,
      radioUrl: 'http://jazzradio.ice.infomaniak.ch/jazzradio-high.mp3',
      urls: ['http://jazzradio.ice.infomaniak.ch/jazzradio-high.mp3']
    };

    this.handleButtonSnow = this.handleButtonSnow.bind(this)
    this.handleButtonRain = this.handleButtonRain.bind(this)
    this.updatePlayer = this.updatePlayer.bind(this)

    history.listen((location) => {
      props.dispatch(clearMessage()); // clear message when changing location
    });
  }

  handleButtonSnow(){
    if ( this.state.snow == 0 ){
      this.setState({
        snow: 100,
        terraincolor: '#ffffff',
        fieldcolor: '#dedede',
        treecolor: '#ffffff',
        state: 1,
        numberclouds: 50,
        cloudscolor: '#ffffff',
        roadcolor: '#ffffff'
      })
    }
    else
    {
      this.setState({
        snow: 0,
        terraincolor: '#516e20',
        fieldcolor: '#fcc305',
        treecolor: '#006335',
        numberclouds: 0,
        cloudscolor: '#ffffff',
        roadcolor: '#544c3e'
      })
    }
  }
  handleButtonRain(){
    if ( this.state.rain == 0 ){
      this.setState({
        rain: 100,
        terraincolor: '#095c02',
        fieldcolor: '#542704',
        treecolor: '#024712',
        numberclouds: 50,
        cloudcolors: '#ffffff'
      })
    }
    else
    {
      this.setState({
        rain: 0,
        numberclouds: 0,
        terraincolor: '#516e20',
        fieldcolor: '#fcc305',
        treecolor: '#006335',
        numberclouds: 0,
        cloudscolor: '#ffffff'
      })
    }
  }

  /*getRadioState(){


    this.setState({
      playing: statez.bears
    });
    return null
  }*/

  updatePlayer() {
    let radiourls = this.state.urls
    console.log("URLS are in player "+radiourls)
    const radioUrls = ['http://jazzradio.ice.infomaniak.ch/jazzradio-high.mp3', 'http://listen.livestreamingservice.com/181-yachtrock_128k.mp3', 'https://olimpicastereo.com.co/wp-content/uploads/2018/04/Christian-Nodal-De-Los-Besos-Que-Te-Di.mp3?_=1']

    this.setState({
      hintPlayerUpdate: this.state.hintPlayerUpdate + 5,
      radiocounter: this.state.radiocounter + 1
    })

    if ((this.state.radiocounter/2) < radioUrls.length){
      if (this.state.playing == false){
        this.setState({
          playing: true,
          radioUrl: radioUrls[this.state.radiocounter/2]
        })
      }
      else
      {
        this.setState({
          playing: false,
        })
      }
    }
    else
    {
      this.setState({
        playing: false,
        radiocounter: 0
      })
    }
  }

  async componentDidMount() {

    //axios
    var data = '';

    var config = {
      method: 'get',
      url: 'http://localhost:8087/radio',
      headers: { },
      data : data
    };

    let radioarray

    await axios(config).then(function (response) {
      const radio = response.data
      radioarray = radio
    })
    .catch(function (error) {
      console.log(error);
    });



    console.log("radiodata " +JSON.stringify(radioarray))
    const radioCleaned = radioarray.map(item => {
      console.log(item)
    })



    const user = this.props.user;

    if (user) {
      this.setState({
        currentUser: user,
        showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }

  }

  logOut() {
    this.props.dispatch(logout());
  }

  render() {
    const { currentUser, showModeratorBoard, showAdminBoard } = this.state;
    return (
      <Router history={history}>
        <div>
          <ReactNotification />
          <Player hintPlayerUpdate = { this.state.hintPlayerUpdate } playing = { this.state.playing } radioUrl={ this.state.radioUrl }/>
          <nav className="navbar navbar-expand-lg navbar-light bg-transparent">
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <Link to={"/"} className="navbar-brand">
              <p>{this.state.radiocounter}</p>            
            </Link>
            <div className="navbar-nav mr-auto">
              <li className="nav-item">
                <Link to={"/home"} className="nav-link">
                  Home
                </Link>
              </li>

              {showModeratorBoard && (
                <li className="nav-item">
                  <Link to={"/mod"} className="nav-link">
                    Moderator Board
                  </Link>
                </li>
              )}

              {showAdminBoard && (
                <li className="nav-item">
                  <Link to={"/admin"} className="nav-link">
                    Admin Board
                  </Link>
                </li>
              )}

              {currentUser && (
                <li className="nav-item">
                  <Link to={"/user"} className="nav-link">
                    User
                  </Link>
                </li>
              )}
              {currentUser && (
                <li className="nav-item">
                  <Link to={"radio"} className="nav-link">
                    Radio
                  </Link>
                </li>
              )}
            </div>

            {currentUser ? (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/profile"} className="nav-link">
                    {currentUser.username}
                  </Link>
                </li>
                <li className="nav-item">
                  <a href="/login" className="nav-link" onClick={this.logOut}>
                    LogOut
                  </a>
                </li>
              </div>
            ) : (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/login"} className="nav-link">
                    Login
                  </Link>
                </li>

                <li className="nav-item">
                  <Link to={"/register"} className="nav-link">
                    Sign Up
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to={"/welcome"} className="nav-link">
                    Welcome
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to={"/radio"} className="nav-link">
                    Radio
                  </Link>
                </li>
                <li className="nav-item">
                  <Link to={"/form"} className="nav-link">
                    Form
                  </Link>
                </li>
              </div>
            )}
            <div className='coucou'>
              <button onClick={this.handleButtonSnow}>Snow</button>
              <button onClick={this.handleButtonRain}>Rain</button>
            </div>
          </div>
          </nav>


          <div className="">
              <Switch>
                <Route exact path="/login" component={Login} />
                <Route exact path="/register" component={Register} />
                <Route exact path="/profile" component={Profile} />
                <Route path="/user" component={BoardUser} />
                <Route path="/welcome" component={Test} />
                <Route path="/form" component={Form} />
                {/* <Route path="/mod" component={BoardModerator} />
                <Route path="/admin" component={BoardAdmin} /> */}
              </Switch>
              <Route
                exact path='/radio'
                render={props => <Radio 
                  updatePlayer={this.updatePlayer}
              />}/>
              <Route
                exact path='/home'
                render={props => <Home 
                  snow={this.state.snow}
                  rain={this.state.rain}
                  terraincolor={this.state.terraincolor}
                  state={this.state.state}
                  fieldcolor={this.state.fieldcolor}
                  treecolor={this.state.treecolor}
                  numberclouds={this.state.numberclouds}
                  cloudscolor={this.state.cloudcolors}
                  location={this.state.location}
                  roadcolor={this.state.roadcolor}
                  town={this.state.town}
                  updatePlayer={this.updatePlayer}
              />}/>
          </div>
        </div>
      </Router>
    );
  }
}

function mapStateToProps(state) {
  const { user } = state.auth;
  return {
    user,
  };
}

export default connect(mapStateToProps)(App);
