import { SET_RADIO, CLEAR_RADIO } from "../actions/types";

const initialState = {
    radio: false
};

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case SET_RADIO:
      return { message: payload };

    case CLEAR_RADIO:
      return { message: "" };

    default:
      return state;
  }
}
