import React, { Component } from 'react'
import { connect } from "react-redux";

export default function Notification (props) {
    console.log(props.notification)
    let displaystatus = "none"
    if (props.notification.length > 0){
        displaystatus = true
    }
    setTimeout(function(){ display = "none" }, 3000)
    return ( 
        <div style={{position: 'fixed', top: '0', right: '0', marginRight: '15px', marginTop:'15px', padding:'1em', fontSize: '15px',  zindex: 3000, backgroundColor: 'red', color: 'black', display: displaystatus}}>
                {props.notification}
        </div>
    )
}
