        }
        this.alert = this.alert.bind(this)
    }
    async alert(){
      //alert(this.state.toto)
    }
    render(){
      const value = this.context
      console.log("VALUE "+value)

        return(
          <group>
            <RadioContext.Provider value={this.state}>
