import react, { useState, setState, useContext } from 'react';
import UserContext from './../context/UserContext';
import axios from 'axios'; 

import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';


function Profile() {

    const [user, setUser] = useState({});
    const { userData, setUserData } = useContext(UserContext);

    const handleChange = e => {
        setUser({...user, [e.target.name]: e.target.value });
        //console.log(user);
    };

    const hanldeEditUser = e => {
        e.preventDefault();
        console.log(user);
        axios
            .put('http://localhost:8087/user/' + user._id, { username: user.username , password: user.password })
            .then(data => {
                // console.log(user);
            })
            .catch( e => console.log(e) );
    };

    return (
        <MDBContainer>
        <MDBRow>
            <MDBCol md="6">
            <form onChange={handleChange} onSubmit={hanldeEditUser}>
                <p className="h4 text-center mb-4">Sign in</p>
                <label htmlFor="defaultFormLoginEmailEx" className="grey-text">
                Your new username
                </label>
                <input type="text" id="defaultFormLoginEmailEx" className="form-control" name="username" label={user.username}/>
                <br />
                <label htmlFor="defaultFormLoginPasswordEx" className="grey-text">
                Your new password
                </label>
                <input type="password" id="defaultFormLoginPasswordEx" className="form-control" name="password" label={user.password}/>
                <div className="text-center mt-4">
                <MDBBtn color="" type="submit">Edite</MDBBtn>
                </div>
            </form>
            </MDBCol>
        </MDBRow>
    </MDBContainer>
    );
}
export default Profile
