import React, { Component } from 'react'
import { connect } from 'react-redux'
import { addRadio } from './actions/radio'

class AddRadio extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            input: "" 
        };
      }

      updateInput = input => {
        this.setState({ input });
      };

      handleAddRadio = () => {
        //this.props.addTodo(this.state.input);
        this.setState({ input: "" });
      };

      render() {
        return (
          <div>
            <input
              onChange={e => this.updateInput(e.target.value)}
              value={this.state.input}
            />
            <button className="add-todo" onClick={this.handleAddRadio}>
              Add Radio
            </button>
          </div>
        );
      }
}

export default connect(
  null,
  { addRadio }
)(AddRadio)
