import React, { Component } from 'react'
import { connect } from "react-redux";

export default function Notification (props) {
    console.log(props.notification)

    const state = {
        color: "white",
        backgroundcolor: "green",
        displaystatus: "none"
    }

    if (props.notification.length > 0){
        state.displaystatus = "block"
        timedOut()
    }
    function timedOut(){
        setTimeout(() => {
            state.displaystatus = "none"
            console.log(state.displaystatus)
        }, 4000);
    }
    return ( 
        <div style={{position: 'fixed', top: '0', right: '0', marginRight: '15px', marginTop:'15px', padding:'1em', fontSize: '15px',  zindex: 3000, backgroundColor: state.backgroundcolor, color: state.color, display: state.displaystatus}}>
                {props.notification}
        </div>
    )
}
