import { useStore } from '../../zustand'
import ReactHowler from 'react-howler'
import React from 'react'

class Player extends Component {
    constructor(props){
        super(props)
        state = {
            bears: 0,
            playing: false
        }
    }
   Getdata = () => {
        const statez = useStore.getState()

        this.setState({
            playing: statez.playing,
            bears: statez.bears
        })
        console.log("PLAYER bears state" + this.state.bears)
        console.log("PLAYER radio state" + this.state.playing)
        console.log("PLAYER radio store" + statez.bears)
        console.log("PLAYER radio store" + statez.bears)

        return true
  }
  render(){
    return (
        <div className="player">
            {this.Getdata() && 
                <ReactHowler
                src='http://jazzradio.ice.infomaniak.ch/jazzradio-high.mp3'
                format= {['mp3', 'aac']}
                html5={true}
                playing={this.state.playing}
                />
            }
        </div>
    );
  }
}
export default Player
