import React, { Component, useState } from 'react'
import Button from './ui/Button.jsx'
import { Redirect } from 'react-router-dom';
import { connect } from "react-redux";
import { BearCounter, useStore } from '../zustand'
import { store } from 'react-notifications-component';
import axios from 'axios'

class Radio extends Component {
    constructor (props) {
        super(props)
        this.state = {
          playing: false,
          url:"",
          name:"",
          shortname:"",
          urlsstorage: []
        }
        this.handlePlay = this.handlePlay.bind(this)
        this.handlePause = this.handlePause.bind(this)
        this.handleChange = this.handleChange.bind(this) 
        this.handleSubmit = this.handleSubmit.bind(this) 
        this.sendRadio = this.sendRadio.bind(this) 
    }
    handlePlay () {
        this.props.updatePlayer()
    }
    handlePause () {
        this.props.updatePlayer()
    }
    sendRadio() {
      var data = JSON.stringify({"url":"http://listen.livestreamingservice.com/181-yachtrock_128k.mp3","name":"YachtRock","shortname":"YTR"});

      var config = {
        method: 'post',
        url: 'http://localhost:8087/radio',
        headers: { 
          'Content-Type': 'application/json'
        },
        data : data
      };

      axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    handleChange(event){ 
      this.setState({ 
        // Computed property names 
        // keys of the objects are computed dynamically 
        [event.target.name] : event.target.value
      })
    } 
    handleSubmit(event){ 
      let urlsstorage = this.state.urlsstorage
      const { url, name, shortname } = this.state 

      console.log("URL SUBSTRING "+url.substring(0, 8))
      //verification
      if (typeof url === 'string' && url instanceof String && url.substring(0, 7) == "http://" && url.substring(0, 8) == "https://"){
        urlsstorage.push({url: url, name: name, shortname: shortname})
      }
      else
      {
        store.addNotification({
          title: "Wunderbar!",
          message: "Vous devez uploader une URL (http/https) valide",
          type: "success",
          insert: "top",
          container: "top-right",
          animationIn: ["animate__animated", "animate__fadeIn"],
          animationOut: ["animate__animated", "animate__fadeOut"],
          dismiss: {
            duration: 3000,
            onScreen: true
          }
        });
      }

      this.setState({ 
        // Computed property names 
        // keys of the objects are computed dynamically 
        urlsstorage: urlsstorage
      })

      console.log(this.state.urlsstorage)
      event.preventDefault()
      alert(` 
        ____Your Details____\n 
        Email : ${url} 
        Name : ${name} 
        Age : ${shortname} 
      `)  
      this.sendRadio()
    } 
    render () {
        const { user: currentUser } = this.props;
        console.log("CURRENT USER : "+JSON.stringify(currentUser)   )
        return (
          <div>
            <Button onClick={this.handlePlay}>Play</Button>
            <Button onClick={this.handlePause}>Pause</Button>
            <form onSubmit={this.handleSubmit}>
            <label>
            {this.state.alertSubmitted}
                URL :
                <input type="text" value={this.state.url} name="url" onChange={this.handleChange}/>
                Nom :
                <input type="text" value={this.state.name} name="name" onChange={this.handleChange}/>
                Nom abbrégé ( 3 lettres max ):
                <input type="text" value={this.state.shortname} name="shortname" onChange={this.handleChange}/>
            </label>
            <button>Create Radio</button> 
            </form>
            <p>newUrl : {this.state.newUrl}</p>
            {String(this.state.urlsstorage)}
          </div>
        )
    }
}

function mapStateToProps(state) {
    const { user } = state.auth;
    return {
      user,
    };
}


export default connect(mapStateToProps)(Radio)
