import React, { Component, useState } from 'react'
import Button from './ui/Button.jsx'
import { Redirect } from 'react-router-dom';
import { connect } from "react-redux";
import { BearCounter, useStore } from '../zustand'
import Notification from "./ui/Notification"

class Radio extends Component {
    constructor (props) {
        super(props)
        this.state = {
          playing: false,
          newUrl: "",
          url:"",
          name:"",
          shortname:"",
          alertSubmitted: "heyyyyy you submited",
          alertValidated: "alert 3 letters",
          alertMissingWord: "heyyyyy word missing"
        }
        this.handlePlay = this.handlePlay.bind(this)
        this.handlePause = this.handlePause.bind(this)
        this.handleChange = this.handleChange.bind(this) 
        //this.handleSubmit = this.handleSubmit.bind(this) 
    }
    notification(notif) {
      return (
        <Notification notification={notif}/>
      )
    }
    handlePlay () {
        this.props.updatePlayer()
    }
    handlePause () {
        this.props.updatePlayer()
    }
    handleChange(event){ 

      if ( this.shortname.length < 4 ){
        this.setState({ 
          // Computed property names 
          // keys of the objects are computed dynamically 
          [event.target.name] : event.target.value
        })
      }
      else
      {
        this.notification("URL must be validated")
      }
      console.log("URL" + this.state.url)
      console.log("NAME" + this.state.name)
      console.log("SHORTNAME" + this.state.shortname)
    } 
    render () {
        const { user: currentUser } = this.props;
        console.log("CURRENT USER : "+JSON.stringify(currentUser)   )
        return (
          <div>
            <Button onClick={this.handlePlay}>Play</Button>
            <Button onClick={this.handlePause}>Pause</Button>
            <form onSubmit={this.handleSubmit}>
            <label>
                URL :
                <input type="text" value={this.state.url} name="url" onChange={this.handleChange}/>
                Nom :
                <input type="text" value={this.state.name} name="name" onChange={this.handleChange}/>
                Nom abbrégé ( 3 lettres max ):
                <input type="text" value={this.state.shortname} name="shortname" onChange={this.handleChange}/>
            </label>
            </form>
            <p>newUrl : {this.state.newUrl}</p>
          </div>
        )
    }
}

function mapStateToProps(state) {
    const { user } = state.auth;
    return {
      user,
    };
}


export default connect(mapStateToProps)(Radio)
