import React, { useState } from 'react'
import { connect } from "react-redux";

export default function Notification (props) {
    console.log(props.notification)

    const colors = {
        color: "white",
        backgroundcolor: "green",
    }

    const [displaystatus, setStatus] = useState("none");

    if (props.notification.length > 0){
        setStatus("block")
        timedOut()
    }

    function timedOut(){
        setTimeout(() => {
            setStatus("none")
        }, 4000);
    }

    return ( 
        <div style={{position: 'fixed', top: '0', right: '0', marginRight: '15px', marginTop:'15px', padding:'1em', fontSize: '15px',  zindex: 3000, backgroundColor: colors.backgroundcolor, color: colors.color, display: displaystatus}}>
                {props.notification}
        </div>
    )
}
