import React, { Component } from 'react'
import Button from './ui/Button.jsx'
import { Redirect } from 'react-router-dom';
import { connect } from "react-redux";
import { BearCounter, useStore } from '../zustand'
import Notification from "./ui/Notification"

class Radio extends Component {
    constructor (props) {
        super(props)
        this.state = {
          playing: false,
          newUrl: "",
          url:"",
          name:"",
          shortname:"",
          alertSubmitted: "heyyyyy you submited",
          alertValidated: "alert 3 letters",
          alertMissingWord: "heyyyyy word missing"
        }
        this.handlePlay = this.handlePlay.bind(this)
        this.handlePause = this.handlePause.bind(this)
    }
    notification(notif) {
      return (
        <Notification notification={notif}/>
      )
    }
    handlePlay () {
        this.props.updatePlayer()
    }
    handlePause () {
        this.props.updatePlayer()
    }
    myChangeHandler = (event) => {
        this.setState({newUrl: event.target.value});
        console.log("EVENT. TARGET .VALUE . " + event.target.value.name)
    }
    render () {
        const { user: currentUser } = this.props;
        console.log("CURRENT USER : "+JSON.stringify(currentUser)   )
        return (
          <div>
            <Button onClick={this.handlePlay}>Play</Button>
            <Button onClick={this.handlePause}>Pause</Button>
            <form>
            <label>
                URL :
                <input type="text" value={this.state.url} name="url" onChange={this.myChangeHandler}/>
                Nom :
                <input type="text" value={this.state.name} name="name" onChange={this.myChangeHandler}/>
                Nom abbrégé ( 3 lettres max ):
                <input type="text" value={this.state.shortname} name="shortname" onChange={this.myChangeHandler}/>
            </label>
            </form>
            <p>newUrl : {this.state.newUrl}</p>
          </div>
        )
    }
}

function mapStateToProps(state) {
    const { user } = state.auth;
    return {
      user,
    };
}


export default connect(mapStateToProps)(Radio)
