import react, { useState, setState, useContext } from 'react';
import axios from 'axios';
// import {Redirect} from 'react-router';
import UserContext from './../context/UserContext';
// import { useHistory } from 'react-router';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';


function Login() {

    const {userData, setUserData} = useContext(UserContext);
    // const history = useHistory();
    const [user, setUser] = useState({});
    const refresh = e => {};

    const handleChange = e => {
        setUser({...user, [e.target.name]: e.target.value });
        //console.log(user);
    };

    const hanldeSubmit = e => {
        e.preventDefault();
        console.log(user);
        axios
            .post('http://localhost:8087/auth/login', { username: user.username , password: user.password })
            .then(data => {
                // console.log(user);
                localStorage.setItem('auth-token', data.data.token);

                setUserData({
                    token: data.data.token
                    });
            })
            .catch( e => console.log(e) );
    };

    const hasAccess = e => {};

    const protect = e => {};

    return (
        <MDBContainer>
            <MDBRow>
                <MDBCol md="6">
                <form onChange={handleChange} onSubmit={hanldeSubmit}>
                    <p className="h4 text-center mb-4">Sign in</p>
                    <label htmlFor="defaultFormLoginEmailEx" className="grey-text">
                    Your username
                    </label>
                    <input type="email" id="defaultFormLoginEmailEx" className="form-control" name="username"/>
                    <br />
                    <label htmlFor="defaultFormLoginPasswordEx" className="grey-text">
                    Your password
                    </label>
                    <input type="password" id="defaultFormLoginPasswordEx" className="form-control" name="password"/>
                    <div className="text-center mt-4">
                    <MDBBtn color="indigo" type="submit">Login</MDBBtn>
                    </div>
                </form>
                </MDBCol>
            </MDBRow>
        </MDBContainer>
    );
};
