import { ADD_RADIO, TOGGLE_RADIO, SET_FILTER } from "./types";

let nextRadioId = 0;

export const addRadio = content => ({
  type: ADD_RADIO,
  payload: {
    id: ++nextRadioId,
    content
  }
});

export const toggleRadio = id => ({
  type: TOGGLE_RADIO,
  payload: { id }
});

export const setFilter = filter => ({ type: SET_FILTER, payload: { filter } });
